import './shortener.scss';
import React from 'react';
import { useState } from 'react';

function Shortener() {

    const [url, setUrl] = useState("");
    const [error, setError] = useState(false)
    const [show, setShow] = useState(false);

    const handleSubmit = () => {
        setShow(true);

        if (url.length === 0) {
            setError(true);
        }

    }



    return (
        <div className='shortener'>
            <div className='inputBox'>
                <div className='left'>
                    <input type='text' placeholder='Shorten a link here...' onChange={(e) => {setUrl(e.target.value)}}/>
                    {error ? <p>Please add a Link</p>:""}
                    <p>Please add a Link</p>
                </div>
                <div className='right'>
                    <button onClick={ handleSubmit }>Shorten It!</button>
                </div>
            </div>
            {
                show && 
            <div className='output'>
                <div className='outputBox'>
                    <div className='originalLink'>
                        <p>{url}</p>
                    </div>
                    <div className='shortLink'>
                        <p>https://www.google.com</p>
                        <button>Copy</button>
                    </div>
                </div>
            </div>}

        </div>
    )
}

export default Shortener
